import React,{ Component } from 'react';
import axios from 'axios';

export default class SupplierDetails extends Component{

    constructor(props){
        super(props);

        this.state={
            invsupplier:{}
        };
    }

    componentDidMount(){
        const invsupplierid=this.props.match.params.id;

        axios.get(`http://localhost:8951/inssupplier/${invsupplierid}`).then((res)=>{
            if(res.data.success){
                this.setState({
                    invsupplier:res.data.invsupplier
                });
                console.log(this.state.invsupplier);
            }
        });
    }
  render(){

    const {inv_supplierID,inv_supplierName,inv_supplierTel,inv_supplierAddress}=this.state.invsupplier;
    return (
     
     <div style={{marginTop:'20px'}} className="container">
         <h4>{inv_supplierName}</h4>
         <h4>{inv_supplierID}</h4>
         <hr/>

         <dl className="row">
             <dt className="col-sm-3"> Contact No</dt>
             <dd className="col-sm-9">{inv_supplierTel}</dd>

             <dt className="col-sm-3">Address</dt>
             <dd className="col-sm-9">{inv_supplierAddress}</dd>

            
         </dl>
    

    

        <form class="row g-3">
            <div class="col-md-4">
            <label for="validationServer01" class="form-label">Supplier ID</label>
            <input type="text" className="form-control is-valid" id="validationServer01" required/>
            <div class="valid-feedback">
                Filled
            </div>
            </div>

            <div class="col-md-4">
            <label for="validationServer02" class="form-label">Supplier Name</label>
            <input type="text" class="form-control is-valid" id="validationServer02" value="Otto" required/>
            <div class="valid-feedback">
                 Filled
            </div>
            </div>
            
            <div class="col-md-4">
            <label for="validationServer02" class="form-label">Contact No</label>
            <input type="text" class="form-control is-valid" id="validationServer02" value="Otto" required/>
            <div class="valid-feedback">
                 Filled
            </div>
            </div>

            <div class="col-md-4">
            <label for="validationServer02" class="form-label">Address </label>
            <input type="text" class="form-control is-valid" id="validationServer02" value="Otto" required/>
            <div class="valid-feedback">
                 Filled
            </div>
            </div>



            <div class="col-12">
            <button class="btn btn-primary" type="submit">Save</button>
            </div>
        </form>
    </div>
   
    )
  }
}
