import React,{ Component } from 'react';
import axios from 'axios';


export default class SupplierDetails extends Component{

    constructor(props){
        super(props);

        this.state={
            invsuppliers:[]
          };


        this.state={
            inv_supplierID:"",
            inv_supplierName:"",
            inv_supplierTel:"",
            inv_supplierAddress:""
        }
    }



    handleSupplierChange=(supp)=>{
        const {name,value} = supp.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }

    onSupplierSave=(supp)=>{
        supp.preventDefault();

        const {inv_supplierID,inv_supplierName,inv_supplierTel,inv_supplierAddress}=this.state;

        const suppdata={
            inv_supplierID:inv_supplierID,
            inv_supplierName:inv_supplierName,
            inv_supplierTel:inv_supplierTel,
            inv_supplierAddress:inv_supplierAddress
        }

        console.log(suppdata)

        axios.post(`http://localhost:8951/inssupplier/save`,suppdata).then((res)=>{
            if(res.data.success){
                this.setState(
                    {
                        inv_supplierID:"",
                        inv_supplierName:"",
                        inv_supplierTel:"",
                        inv_supplierAddress:""
                    }
                )
            }
        })
    }

    componentDidMount(){
        this.retrieveSuppliers();
    }

    retrieveSuppliers(){
     axios.get("http://localhost:8951/inssuppliers").then(res=>{
        if(res.data.success){
        this.setState({
          invsuppliers:res.data.existingInvSuppliers
        });
        console.log(this.state.invsuppliers)
      }
    });
    }

    onDeleteSuppliers = (invsuppid)=>{

    

        axios.delete(`http://localhost:8951/inssupplier/delete/${invsuppid}`).then((res)=>{
          alert("Supplier Deleted from the List Successfully");
          this.retrieveSuppliers();
        })
      }

  render(){
    return (
     
     <div className="col-md-8 mt-4 mx-auto">
         <h1 className= "h3 mb-3 font-weight-normal">Insert New Suppliers</h1>
         <form className="needs-validation" noValidate>
             <div className="form-group" style={{marginBottom:'15px'}}>
                 <label style={{marginBottom:'5px'}}> Supplier ID</label>
                 <input type="text"
                 className="form-control"
                 name="inv_supplierID"
                 placeholder="SUPPLIER ID here"
                 value={this.state.inv_supplierID}
                 onChange={this.handleSupplierChange}/>

             </div>

             <div className="form-group" style={{marginBottom:'15px'}}>
                 <label style={{marginBottom:'5px'}}> Supplier Name</label>
                 <input type="text"
                 className="form-control"
                 name="inv_supplierName"
                 placeholder="SUPPLIER NAME here"
                 value={this.state.inv_supplierName}
                 onChange={this.handleSupplierChange}/>

             </div>

             <div className="form-group" style={{marginBottom:'15px'}}>
                 <label style={{marginBottom:'5px'}}> Contact No</label>
                 <input type="text"
                 className="form-control"
                 name="inv_supplierTel"
                 placeholder="CONTACT No here"
                 value={this.state.inv_supplierTel}
                 onChange={this.handleSupplierChange}/>

             </div>

             <div className="form-group" style={{marginBottom:'15px'}}>
                 <label style={{marginBottom:'5px'}}> Address</label>
                 <input type="text"
                 className="form-control"
                 name="inv_supplierAddress"
                 placeholder="ADDRESS here"
                 value={this.state.inv_supplierAddress}
                 onChange={this.handleSupplierChange}/>

             </div>

            

            
             <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSupplierSave}>
                   <i className="far fa-check-square"></i>&nbsp; Add 
             </button>
         </form>
       

         <div>
         <table className="table table-hover" style={{marginTop:'40px'}} >
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Supplier ID</th> 
            </tr>
          </thead>
          <tbody>
            {this.state.invsuppliers.map((invsuppliers,ssindex)=>(
              <tr key={ssindex}>
                <th scope="row">{ssindex+1}</th>

                <td>
                    <a href={'/invsupplierDetails'} style={{textDecoration:'none'}}>
                           {invsuppliers.inv_supplierID}
                    </a>   

                    <div class="toast" role="alert" aria-live="assertive" aria-atomic="true">
                        <div class="toast-header">
                            <img src="" class="rounded me-2" alt="..."><i className="fas fa-user"></i></img>
                            <strong className="me-auto">{invsuppliers.inv_supplierID}</strong>
                            <small class="text-muted">11 mins ago</small>
                            <button type="button" class="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                        </div>
                        <div className="toast-body">
                              {invsuppliers.inv_supplierName}<br/>
                              {invsuppliers.inv_supplierTel}<br/>
                              {invsuppliers.inv_supplierAddress}<br/>
                        </div>
                        </div> 
                </td>
                    
                <td>
                  <a className="btn btn-warning" href={`//${invsuppliers._id}`}>
                    <i className="fas fa-edit"></i>
                  </a> 
                  &nbsp;
                  <a className="btn btn-danger" href="#" onClick={()=> this.onDeleteSuppliers(iorders._id)}>
                    <i className="far fa-trash-alt"></i>
                  </a>

                </td>
              </tr>
            ))}
          </tbody>

             </table>
         </div>
     </div>
    
    )
  }
}
